import React, { useState, useEffect } from 'react';
import Modal from './Modal';

const GetApi = () => {

    const [products, setProducts] = useState([]);
    const [modalShow, setModalShow] = useState(false);

    const fetchData = () => {
      fetch("https://fakestoreapi.com/products")
        .then(response => {
          return response.json()
        })
        .then(data => {
            setProducts(data)
        })
    }
  
    useEffect(() => {
      fetchData()
    }, [])

const deleteRow = (event) => {  

      let x = event.target;
        x.parentNode.parentNode.style.opacity = "1";
        if (x.parentNode.parentNode.className === "post") {              
          let intervalID;  
          const deletePost = () => { x.parentNode.parentNode.style.opacity -= "0.1"; }
          intervalID = setInterval(deletePost, 100)
      } 
    }  
  
    return (
      <div>
        {products.length > 0 && (
          <table>
            <tr>
                <th><h1>ID</h1></th>
                <th><h1>Title</h1></th>
                <th><h1>Price</h1></th>
                <th><h1>Action</h1></th>
            </tr>
            {products.map(product => (
              <Modal.Body>
              <tr key={product.id} className="post">
                  <td>{product.id}</td>
                  <td>{product.title}</td>
                  <td>{product.price}</td>
                  <td>
                  <button className="btn btn-success" style={{"margin-right": "10px"}} onClick={() => setModalShow(true)}>View</button>
                  <button className="btn btn-danger" onClick={(event) => deleteRow(event)}>Delete</button> 
                  </td>
              </tr>
              </Modal.Body>
            ))}
          </table>
        )}
    <Modal
      show={modalShow}
      onHide={() => setModalShow(false)}
    />
      </div>
    )
  }

export default GetApi;
