import React, { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

const GetApi = () => {

    const [products, setProducts] = useState([]);
    const [modalShow, setModalShow] = useState(false);
    
    const fetchData = () => {
      fetch("https://fakestoreapi.com/products")
        .then(response => {
          return response.json()
        })
        .then(data => {
            setProducts(data)
        })
    }
  
    useEffect(() => {
      fetchData()
    }, [])

    const deleteRow = (event) => {  

      let x = event.target;
        x.parentNode.parentNode.style.opacity = "1";
        if (x.parentNode.parentNode.className === "post") {              
          let intervalID;  
          const deletePost = () => { x.parentNode.parentNode.style.opacity -= "0.1"; }
          intervalID = setInterval(deletePost, 100)
      } 
    }  

    function MyVerticallyCenteredModal(props, product, id, title, price) {
      
      console.log(title[1]);

      return (
        <Modal
          {...props}
          size="lg"
          aria-labelledby="contained-modal-title-vcenter"
          centered
        >
          <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
              Modal heading
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <h4>Centered Modal</h4>
          </Modal.Body>
          <Modal.Footer>
            <Button onClick={props.onHide}>Close</Button>
          </Modal.Footer>
        </Modal>
      );
    }

 

    return (
      <div>
        {products.length > 0 && (
          <table>
            <tbody>
            <tr>
                <th><h1>ID</h1></th>
                <th><h1>Title</h1></th>
                <th><h1>Price</h1></th>
                <th><h1>Action</h1></th>
            </tr>
            {products.map(product => (
              <tr key={product.id} className="post">
                  <td>{product.id}</td>
                  <td>{product.title}</td>
                  <td>{product.price}</td>
                  <td>
                  <button  className="btn btn-success" onClick={() => MyVerticallyCenteredModal(setModalShow(true), product, product.id, product.title, product.price) } style={{"marginRight": "10px"}}>
                  View
                 </button>
                  <button className="btn btn-danger" onClick={(event) => deleteRow(event)}>Delete</button>  
                  </td>
              </tr>
            ))}
            </tbody>
          </table>
        )}
             <MyVerticallyCenteredModal
        show={modalShow}
        onHide={() => setModalShow(false)}
      />
      </div>
    )
  }

export default GetApi;
