import React, { useState, useEffect } from 'react';

const GetApi = () => {

    const [products, setProducts] = useState([])

    const fetchData = () => {
      fetch("https://fakestoreapi.com/products")
        .then(response => {
          return response.json()
        })
        .then(data => {
            setProducts(data)
        })
    }
  
    useEffect(() => {
      fetchData()
    }, [])

    const deleteRow = (id, e) => {  
      axios.delete(`https://fakestoreapi.com/products/${id}`)  
        .then(res => {  
          console.log(res);  
          console.log(res.data);  
      
          const posts = products.filter(item => item.id !== id);  
          this.setState({ posts });  
        })  
      
    }  
  
    return (
      <div>
        {products.length > 0 && (
          <table>
            <tr>
                <th><h1>ID</h1></th>
                <th><h1>Title</h1></th>
                <th><h1>Price</h1></th>
                <th><h1>Action</h1></th>
            </tr>
            {products.map(product => (
              <tr key={product.id}>
                  <td>{product.id}</td>
                  <td>{product.title}</td>
                  <td>{product.price}</td>
                  <td>
                  <button className="btn btn-danger" onClick={(e) => this.deleteRow(product.id, e)}>Delete</button>  
                  </td>
              </tr>
            ))}
          </table>
        )}
      </div>
    )
  }

export default GetApi;
